// SPDX-License-Identifier: MIT
pragma solidity >=0.4.22 <0.9.0;

import "../node_modules/openzeppelin-solidity/contracts/math/SafeMath.sol";

contract FlightSuretyData {
    using SafeMath for uint256;

    /********************************************************************************************/
    /*                                       DATA VARIABLES                                     */
    /********************************************************************************************/

    // Account used to deploy contract
    address private contractOwner;

    // Blocks all state changes throughout the contract if false
    bool private operational = true;

    // mapping(address => bool) private authorizedCallers;

    struct Airline
    {
        bool isRegistered;
        address ethAccount;
        uint256 owner;
    }

    struct Flight
    {
        string flightName;
        address airline;
        uint8 statusCode;
        uint256 timestamp;
    }

    struct Insurance
    {
        address passenger;
        uint256 insuranceAmount;
    }


    mapping(address => uint8) authorizedCaller;
    mapping(address => Airline) airlines;
    mapping(string => Flight) flights;
    mapping(string => address[]) flightInsurees;
    mapping(address => uint256) funds;
    mapping(bytes32 => uint256) flightSurety;

    /********************************************************************************************/
    /*                                       EVENT DEFINITIONS                                  */
    /********************************************************************************************/

    event AuthorizedCaller(address caller);
    event DeAuthorizedCaller(address caller);
    event CreditInsured(address passenger, string flight, uint256 amount);
    event RegisterAirline(address indexed account);
    event RegisterFlight(string indexed account);

    /**
    * @dev Constructor
    *      The deploying account becomes contractOwner
    */
    constructor() 
    public 
    {
        contractOwner = msg.sender;
        // airlines[contractOwner] = Airline(contractOwner, AirlineState.Paid, "The first airline", 0);
        // totalPaidAirlines++;
    }

    /********************************************************************************************/
    /*                                       FUNCTION MODIFIERS                                 */
    /********************************************************************************************/

    // Modifiers help avoid duplication of code. They are typically used to validate something
    // before a function is allowed to be executed.

    /**
    * @dev Modifier that requires the "operational" boolean variable to be "true"
    *      This is used on all state changing functions to pause the contract in 
    *      the event there is an issue that needs to be fixed
    */
    modifier requireIsOperational() 
    {
        require(operational, "Contract is currently not operational");
        _;  // All modifiers require an "_" which indicates where the function body will be added
    }

    /**
    * @dev Modifier that requires the "ContractOwner" account to be the function caller
    */
    modifier requireContractOwner()
    {
        require(msg.sender == contractOwner, "Caller is not contract owner");
        _;
    }

    /********************************************************************************************/
    /*                                       UTILITY FUNCTIONS                                  */
    /********************************************************************************************/

    /**
    * @dev Get operating status of contract
    *
    * @return A bool that is the current operating status
    */      
    function isOperational() 
    public 
    view 
    returns(bool) 
    {
        return operational;
    }


    /**
    * @dev Sets contract operations on/off
    *
    * When operational mode is disabled, all write transactions except for this one will fail
    */    
    function setOperatingStatus(
        bool mode
    ) 
    external
    requireContractOwner 
    {
        operational = mode;
    }

    /********************************************************************************************/
    /*                                     SMART CONTRACT FUNCTIONS                             */
    /********************************************************************************************/

   /**
    * @dev Add an airline to the registration queue
    *      Can only be called from FlightSuretyApp contract
    *
    */   
    function registerAirline()
    external
    pure
    {}


   /**
    * @dev Buy insurance for a flight
    *
    */   
    function buy()
    external
    payable
    {}


    /**
     *  @dev Credits payouts to insurees
    */
    function creditInsurees()
    external
    pure
    {}


    /**
     *  @dev Transfers eligible payout funds to insuree
     *
    */
    function pay()
    external
    pure
    {}

   /**
    * @dev Initial funding for the insurance. Unless there are too many delayed flights
    *      resulting in insurance payouts, the contract should be self-sustaining
    *
    */   
    function fund()
    public
    payable
    {}


    function getFlightKey(
        address airline,
        string memory flight,
        uint256 timestamp
    )
    pure
    internal
    returns(bytes32) 
    {
        return keccak256(abi.encodePacked(airline, flight, timestamp));
    }

    /**
    * @dev Fallback function for funding smart contract.
    *
    */
    function()
    external 
    payable 
    {
        fund();
    }

}

